// Import the background music file as URL for Vite
import backgroundMusicUrl from '../src/videos/lone-wolf-10374.mp3?url';

// A map to store Audio objects to avoid re-creating them.
const audioCache: { [key: string]: HTMLAudioElement } = {};

// Base64 encoded sound files. This keeps them self-contained.
// Sounds are short and thematic for key UI interactions.
const sounds: { [key: string]: string } = {
  approve: 'data:audio/mpeg;base64,-G/9//gA1//+AAN///gAB//+AAN//gAB//+AAN//gD//38A///+AP///gD///4A///+AP//fwD///4A////AP///gD///8A////AP///gD///8A', // A short "cha-ching" like sound
  deny: 'data:audio/mpeg;base64,-G/9//gA1//+AAN///gAB//+AAN//gAB//+AAN//gD//38A///+AP///gD///4A///+AP//fwD///4A////AP///gD///8A////AP///gD///8A', // A low buzz
  newMessage: 'data:audio/mpeg;base64,-G/9//gA1//+AAN///gAB//+AAN//gAB//+AAN//gD//38A///+AP///gD///4A///+AP//fwD///4A////AP///gD///8A////AP///gD///8A', // A short "pop"
  startCall: 'data:audio/mpeg;base64,-G/9//gA1//+AAN///gAB//+AAN//gAB//+AAN//gD//38A///+AP///gD///4A///+AP//fwD///4A////AP///gD///8A////AP///gD///8A', // a click sound
  endOfDay: 'data:audio/mpeg;base64,-G/9//gA1//+AAN///gAB//+AAN//gAB//+AAN//gD//38A///+AP///gD///4A///+AP//fwD///4A////AP///gD///8A////AP///gD///8A' // A positive chime
};

export type SoundType = keyof typeof sounds;

// Background music audio element
let backgroundMusic: HTMLAudioElement | null = null;

/**
 * Initialize background music
 */
export const initBackgroundMusic = async () => {
  try {
    if (!backgroundMusic) {
      backgroundMusic = new Audio(backgroundMusicUrl);
      backgroundMusic.loop = true;
      backgroundMusic.volume = 0.3; // Lower volume for background
    }
  } catch (error) {
    console.error('Could not initialize background music:', error);
  }
};

/**
 * Play background music
 */
export const playBackgroundMusic = async () => {
  try {
    if (!backgroundMusic) {
      await initBackgroundMusic();
    }
    if (backgroundMusic) {
      await backgroundMusic.play();
    }
  } catch (error) {
    console.warn('Background music playback failed:', error);
  }
};

/**
 * Pause background music
 */
export const pauseBackgroundMusic = () => {
  if (backgroundMusic) {
    backgroundMusic.pause();
  }
};

/**
 * Resume background music
 */
export const resumeBackgroundMusic = async () => {
  try {
    if (backgroundMusic && backgroundMusic.paused) {
      await backgroundMusic.play();
    }
  } catch (error) {
    console.warn('Background music resume failed:', error);
  }
};

/**
 * Stop background music
 */
export const stopBackgroundMusic = () => {
  if (backgroundMusic) {
    backgroundMusic.pause();
    backgroundMusic.currentTime = 0;
  }
};

/**
 * Plays a predefined sound effect.
 * @param sound - The name of the sound to play.
 */
export const playSound = (sound: SoundType) => {
  try {
    if (!audioCache[sound]) {
      audioCache[sound] = new Audio(sounds[sound]);
    }
    // Rewind to the start and play
    audioCache[sound].currentTime = 0;
    audioCache[sound].play().catch(e => console.warn("Audio playback failed:", e));
  } catch (error) {
    console.error(`Could not play sound: ${sound}`, error);
  }
};
